package org.obinject.sample.album; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.meta.generator.DistanceUtil; import org.obinject.meta.Point; import org.obinject.meta.Uuid; import org.obinject.storage.KeyStructure; public class PointOneLocal extends $Local implements Point<PointOneLocal> { private double preservedDistance; private static Uuid classId; public static Uuid getClassId(){ if (PointOneLocal.classId == null) {PointOneLocal.classId = Uuid.fromString("840E72FD-6291-9FAB-CF7D-AF598BCB3FD9"); }return PointOneLocal.classId; } public PointOneLocal(){} public PointOneLocal($Local obj){ super(obj); } public PointOneLocal(Local obj, Uuid uuid){ super(obj, uuid); } public PointOneLocal(Uuid uuid){ super(uuid); } @Override public boolean hasSameKey(PointOneLocal obj) { int i=0; while(( i < this.numberOfDimensions()) && (this.getOrigin(i) == obj.getOrigin(i))){ i++; }return i == this.numberOfDimensions(); }@Override public double getOrigin(int axis){ switch(axis){ case 0: return getLatitudePadrao(); case 1: return getLongitudePadrao(); default: return 0; } } @Override public void setOrigin(int axis, double value){ switch(axis){ case 0: setLatitudePadrao(value); break; case 1: setLongitudePadrao(value); break; } } @Override public double getPreservedDistance() { return this.preservedDistance; } @Override public int numberOfDimensions(){ return 2;} @Override public void setPreservedDistance(double distance) { this.preservedDistance = distance; } @Override public double distanceTo(PointOneLocal obj){ return DistanceUtil.euclidean(this, obj); } @Override public KeyStructure<PointOneLocal> getKeyStructure() { return pointOneLocalStructure; } @Override public boolean pullKey(byte[] array, int position) { PullPage pull = new PullPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ this.setOrigin(i, pull.pullDouble()); } return true; } @Override public void pushKey(byte[] array, int position){ PushPage push = new PushPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ push.pushDouble(this.getOrigin(i));} } @Override public int sizeOfKey() { return Page.sizeOfDouble * this.numberOfDimensions();} } 