package org.obinject.sample.album; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.meta.Sort; import org.obinject.meta.Uuid; import org.obinject.storage.KeyStructure; public class UniqueOneLocal extends $Local implements Sort<UniqueOneLocal>, Comparable<UniqueOneLocal> { private static Uuid classId; public static Uuid getClassId(){ if (UniqueOneLocal.classId == null) {UniqueOneLocal.classId = Uuid.fromString("5F260688-C0BE-FD52-E45F-1B9A084B339C"); }return UniqueOneLocal.classId; } public UniqueOneLocal(){} public UniqueOneLocal($Local obj){ super(obj); } public UniqueOneLocal(Local obj, Uuid uuid){ super(obj, uuid); } public UniqueOneLocal(Uuid uuid){ super(uuid); } @Override public boolean hasSameKey(UniqueOneLocal obj) { return (this.getCodigo() == obj.getCodigo()); } @Override public int compareTo(UniqueOneLocal obj) { if ((this.getCodigo() < obj.getCodigo())) { return -1; } else if ((this.getCodigo() == obj.getCodigo())) { return 0; } else {return 1; } } @Override public KeyStructure<UniqueOneLocal> getKeyStructure() { return uniqueOneLocalStructure; } @Override public boolean pullKey(byte[] array, int position) { PullPage pull = new PullPage(array, position); this.setCodigo(pull.pullInteger()); return true; } @Override public void pushKey(byte[] array, int position){ PushPage push = new PushPage(array, position); push.pushInteger(this.getCodigo()); } @Override public int sizeOfKey() { return  + Page.sizeOfInteger; } } 