package org.obinject.sample.celular; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.meta.generator.DistanceUtil; import org.obinject.meta.Point; import org.obinject.meta.Uuid; import org.obinject.storage.KeyStructure; public class PointOneCelular extends $Celular implements Point<PointOneCelular> { private double preservedDistance; private static Uuid classId; public static Uuid getClassId(){ if (PointOneCelular.classId == null) {PointOneCelular.classId = Uuid.fromString("6DB1A49F-ED0A-7014-48DF-64D391F10437"); }return PointOneCelular.classId; } public PointOneCelular(){} public PointOneCelular($Celular obj){ super(obj); } public PointOneCelular(Celular obj, Uuid uuid){ super(obj, uuid); } public PointOneCelular(Uuid uuid){ super(uuid); } @Override public boolean hasSameKey(PointOneCelular obj) { int i=0; while(( i < this.numberOfDimensions()) && (this.getOrigin(i) == obj.getOrigin(i))){ i++; }return i == this.numberOfDimensions(); }@Override public double getOrigin(int axis){ switch(axis){ case 0: return getLatitude(); case 1: return getLongitude(); default: return 0; } } @Override public void setOrigin(int axis, double value){ switch(axis){ case 0: setLatitude(value); break; case 1: setLongitude(value); break; } } @Override public double getPreservedDistance() { return this.preservedDistance; } @Override public int numberOfDimensions(){ return 2;} @Override public void setPreservedDistance(double distance) { this.preservedDistance = distance; } @Override public double distanceTo(PointOneCelular obj){ return DistanceUtil.euclidean(this, obj); } @Override public KeyStructure<PointOneCelular> getKeyStructure() { return pointOneCelularStructure; } @Override public boolean pullKey(byte[] array, int position) { PullPage pull = new PullPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ this.setOrigin(i, pull.pullDouble()); } return true; } @Override public void pushKey(byte[] array, int position){ PushPage push = new PushPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ push.pushDouble(this.getOrigin(i));} } @Override public int sizeOfKey() { return Page.sizeOfDouble * this.numberOfDimensions();} } 