package org.obinject.sample.elections; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.device.File; import org.obinject.exception.TransientObjectException; import org.obinject.meta.Entity; import org.obinject.meta.Uuid; import org.obinject.queries.Attribute; import org.obinject.queries.Schema; import org.obinject.storage.BTree; import org.obinject.storage.BTreeEntity; import org.obinject.storage.EntityStructure; import org.obinject.storage.KeyStructure; import org.obinject.storage.Structure; public class $Person extends Person implements Entity<$Person> { protected Uuid uuid; private static Uuid classId; public static Uuid getClassId(){ if ($Person.classId == null) {$Person.classId = Uuid.fromString("62B82097-3DC8-BB8D-B672-BCD0ADDCC6AD"); }return $Person.classId; } public static final BTreeEntity<$Person> entityStructure = new BTreeEntity<$Person>(new File("build/classes/org/obinject/sample/elections/elections.dbo", 4096)){}; public static final BTree<UniqueOnePerson> uniqueOnePersonStructure = new BTree<UniqueOnePerson>(new File("build/classes/org/obinject/sample/elections/elections.dbo", 4096)){}; public static final Attribute<Integer> identityCard = new Attribute<Integer> (){ @Override public Integer valueOfAttribute(Entity entity) { return ((Person)entity).getIdentityCard(); } }; public static final Attribute<java.lang.String> name = new Attribute<java.lang.String> (){ @Override public java.lang.String valueOfAttribute(Entity entity) { return ((Person)entity).getName(); } }; public static final Attribute<java.lang.String> adress = new Attribute<java.lang.String> (){ @Override public java.lang.String valueOfAttribute(Entity entity) { return ((Person)entity).getAdress(); } }; public $Person(){ this.uuid = Uuid.generator(); } public $Person(Person obj){ this.setIdentityCard(obj.getIdentityCard()); this.setName(obj.getName()); this.setAdress(obj.getAdress()); this.uuid = Uuid.generator(); } public $Person(Person obj, Uuid uuid){ this.setIdentityCard(obj.getIdentityCard()); this.setName(obj.getName()); this.setAdress(obj.getAdress()); this.uuid = uuid; } public $Person($Person obj){ this.setIdentityCard(obj.getIdentityCard()); this.setName(obj.getName()); this.setAdress(obj.getAdress()); this.uuid = obj.getUuid();; } public $Person(Uuid uuid){ this.uuid = uuid; } @Override public boolean isEqual($Person obj) { return (this.getIdentityCard() == obj.getIdentityCard()) && (((this.getName() == null) && (obj.getName() == null)) || ((this.getName() != null) && (obj.getName() != null) && (this.getName().equals( obj.getName())))) && (((this.getAdress() == null) && (obj.getAdress() == null)) || ((this.getAdress() != null) && (obj.getAdress() != null) && (this.getAdress().equals( obj.getAdress())))); } @Override public Uuid getUuid() { return this.uuid; } @Override public EntityStructure<$Person> getEntityStructure() { return entityStructure; } @Override public boolean inject(){ Uuid uuidInject = $Person.find(this); if(uuidInject == null){ $Person.entityStructure.add(this); UniqueOnePerson.uniqueOnePersonStructure.add(new UniqueOnePerson(this, this.getUuid())); return true; }else{ this.uuid = uuidInject; return false; } } @Override public boolean reject(){ Uuid uuidReject = $Person.find(this); if(uuidReject != null){ UniqueOnePerson.uniqueOnePersonStructure.remove(new UniqueOnePerson(this, this.getUuid())); $Person.entityStructure.remove(this); return true; }else{ return false; } } @Override public boolean modify(){ Uuid uuidOld = $Person.find(this); $Person entityOld = $Person.entityStructure.find(uuidOld); if(entityOld != null){ UniqueOnePerson uniqueOnePersonOld = new UniqueOnePerson(entityOld, entityOld.getUuid()); UniqueOnePerson uniqueOnePersonNew = new UniqueOnePerson(this, entityOld.getUuid()); if(uniqueOnePersonOld.hasSameKey(uniqueOnePersonNew)){ $Person.uniqueOnePersonStructure.remove(uniqueOnePersonOld); $Person.uniqueOnePersonStructure.add(uniqueOnePersonNew); } this.uuid = uuidOld; $Person.entityStructure.remove(entityOld); $Person.entityStructure.add(this); return true; }else{ return false; } } public static Uuid find($Person entity) { UniqueOnePerson unique = new UniqueOnePerson (entity, entity.getUuid()); return $Person.uniqueOnePersonStructure.find(unique); } @Override public boolean pullEntity(byte[] array, int position) { PullPage pull = new PullPage(array, position); Uuid storedClass = pull.pullUuid(); if ($Person.classId.equals(storedClass) == true){ uuid = pull.pullUuid(); this.setIdentityCard(pull.pullInteger()); this.setName(pull.pullString()); this.setAdress(pull.pullString()); return true; } return false; } @Override public void pushEntity(byte[] array, int position){ PushPage push = new PushPage(array, position); push.pushUuid($Person.classId); push.pushUuid(uuid); push.pushInteger(this.getIdentityCard()); push.pushString(this.getName()); push.pushString(this.getAdress()); } @Override public int sizeOfEntity() { return Page.sizeOfUuid + Page.sizeOfUuid  + Page.sizeOfInteger + Page.sizeOfString(this.getName()) + Page.sizeOfString(this.getAdress()); } static{ identityCard.getSchemas().add( new Schema<$Person, UniqueOnePerson, Integer>() { @Override public $Person newEntity(Integer value) { $Person obj = new $Person(); obj.setIdentityCard(value); return obj; } @Override public UniqueOnePerson newKey(Integer value) { UniqueOnePerson obj = new UniqueOnePerson(); obj.setIdentityCard(value); return obj; } @Override public EntityStructure<$Person> getEntityStructure() { return $Person.entityStructure; } @Override public KeyStructure<UniqueOnePerson> getKeyStructure() { return UniqueOnePerson.uniqueOnePersonStructure; } } ); } } 