package org.obinject.sample.exame; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.meta.generator.DistanceUtil; import org.obinject.meta.Point; import org.obinject.meta.Uuid; import org.obinject.storage.KeyStructure; public class CoordinateOneExame extends $Exame implements Point<CoordinateOneExame> { private double preservedDistance; private static Uuid classId; public static Uuid getClassId(){ if (CoordinateOneExame.classId == null) {CoordinateOneExame.classId = Uuid.fromString("0D5D4783-5FC5-61D0-4AF9-524FC6630D68"); }return CoordinateOneExame.classId; } public CoordinateOneExame(){} public CoordinateOneExame($Exame obj){ super(obj); } public CoordinateOneExame(Exame obj, Uuid uuid){ super(obj, uuid); } public CoordinateOneExame(Uuid uuid){ super(uuid); } @Override public boolean hasSameKey(CoordinateOneExame obj) { int i=0; while(( i < this.numberOfDimensions()) && (this.getOrigin(i) == obj.getOrigin(i))){ i++; }return i == this.numberOfDimensions(); }@Override public double getOrigin(int axis){ switch(axis){ case 0: return getLatitudeImagem(); case 1: return getLongitudeImagem(); default: return 0; } } @Override public void setOrigin(int axis, double value){ switch(axis){ case 0: setLatitudeImagem(value); break; case 1: setLongitudeImagem(value); break; } } @Override public double getPreservedDistance() { return this.preservedDistance; } @Override public int numberOfDimensions(){ return 2;} @Override public void setPreservedDistance(double distance) { this.preservedDistance = distance; } @Override public double distanceTo(CoordinateOneExame obj){ return DistanceUtil.sphericalDistanceDegree(this, obj, 6378.0); } @Override public KeyStructure<CoordinateOneExame> getKeyStructure() { return coordinateOneExameStructure; } @Override public boolean pullKey(byte[] array, int position) { PullPage pull = new PullPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ this.setOrigin(i, pull.pullDouble()); } return true; } @Override public void pushKey(byte[] array, int position){ PushPage push = new PushPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ push.pushDouble(this.getOrigin(i));} } @Override public int sizeOfKey() { return Page.sizeOfDouble * this.numberOfDimensions();} } 