package org.obinject.sample.join.pcuk; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.meta.generator.DistanceUtil; import org.obinject.meta.Point; import org.obinject.meta.Uuid; import org.obinject.storage.KeyStructure; public class PointOnePostCode extends $PostCode implements Point<PointOnePostCode> { private double preservedDistance; private static Uuid classId; public static Uuid getClassId(){ if (PointOnePostCode.classId == null) {PointOnePostCode.classId = Uuid.fromString("CA10C356-C0E7-3B7A-9348-3EE5F124B5D4"); }return PointOnePostCode.classId; } public PointOnePostCode(){} public PointOnePostCode($PostCode obj){ super(obj); } public PointOnePostCode(PostCode obj, Uuid uuid){ super(obj, uuid); } public PointOnePostCode(Uuid uuid){ super(uuid); } @Override public boolean hasSameKey(PointOnePostCode obj) { int i=0; while(( i < this.numberOfDimensions()) && (this.getOrigin(i) == obj.getOrigin(i))){ i++; }return i == this.numberOfDimensions(); }@Override public double getOrigin(int axis){ switch(axis){ case 0: return getLatitude(); case 1: return getLongitude(); default: return 0; } } @Override public void setOrigin(int axis, double value){ switch(axis){ case 0: setLatitude(value); break; case 1: setLongitude(value); break; } } @Override public double getPreservedDistance() { return this.preservedDistance; } @Override public int numberOfDimensions(){ return 2;} @Override public void setPreservedDistance(double distance) { this.preservedDistance = distance; } @Override public double distanceTo(PointOnePostCode obj){ return DistanceUtil.euclidean(this, obj); } @Override public KeyStructure<PointOnePostCode> getKeyStructure() { return pointOnePostCodeStructure; } @Override public boolean pullKey(byte[] array, int position) { PullPage pull = new PullPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ this.setOrigin(i, pull.pullDouble()); } return true; } @Override public void pushKey(byte[] array, int position){ PushPage push = new PushPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ push.pushDouble(this.getOrigin(i));} } @Override public int sizeOfKey() { return Page.sizeOfDouble * this.numberOfDimensions();} } 