package org.obinject.sample.join.uniform; import org.obinject.block.Page; import org.obinject.block.PullPage; import org.obinject.block.PushPage; import org.obinject.meta.generator.DistanceUtil; import org.obinject.meta.Point; import org.obinject.meta.Uuid; import org.obinject.storage.KeyStructure; public class PointOnePonto extends $Ponto implements Point<PointOnePonto> { private double preservedDistance; private static Uuid classId; public static Uuid getClassId(){ if (PointOnePonto.classId == null) {PointOnePonto.classId = Uuid.fromString("946583EA-509E-D022-DEF5-A871B7F35858"); }return PointOnePonto.classId; } public PointOnePonto(){} public PointOnePonto($Ponto obj){ super(obj); } public PointOnePonto(Ponto obj, Uuid uuid){ super(obj, uuid); } public PointOnePonto(Uuid uuid){ super(uuid); } @Override public boolean hasSameKey(PointOnePonto obj) { int i=0; while(( i < this.numberOfDimensions()) && (this.getOrigin(i) == obj.getOrigin(i))){ i++; }return i == this.numberOfDimensions(); }@Override public double getOrigin(int axis){ switch(axis){ case 0: return getX(); case 1: return getY(); default: return 0; } } @Override public void setOrigin(int axis, double value){ switch(axis){ case 0: setX((int)value); break; case 1: setY((int)value); break; } } @Override public double getPreservedDistance() { return this.preservedDistance; } @Override public int numberOfDimensions(){ return 2;} @Override public void setPreservedDistance(double distance) { this.preservedDistance = distance; } @Override public double distanceTo(PointOnePonto obj){ return DistanceUtil.euclidean(this, obj); } @Override public KeyStructure<PointOnePonto> getKeyStructure() { return pointOnePontoStructure; } @Override public boolean pullKey(byte[] array, int position) { PullPage pull = new PullPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ this.setOrigin(i, pull.pullDouble()); } return true; } @Override public void pushKey(byte[] array, int position){ PushPage push = new PushPage(array, position); for (int i = 0; i < this.numberOfDimensions(); i++){ push.pushDouble(this.getOrigin(i));} } @Override public int sizeOfKey() { return Page.sizeOfDouble * this.numberOfDimensions();} } 